import React, { createContext, useState} from 'react';
const Data = createContext()

export default function Userdata(props) {
    const [maincolor,setmaincolor] = useState("cyan")
    const [inputdisplay,setinputdisplay] = useState(false)
    const [namecolor,setnamecolor] = useState("#000000")
    const [professioncolor,setprofessioncolor] = useState("#000000")
    const [descriptioncolor,setdescriptioncolor] = useState("#000000")
    const [cvcolor,setcvcolor] = useState("#101010")
    const [pointer,setpointer] = useState(0)
  return (
    <Data.Provider value={{
    maincolor,
    setmaincolor,
    inputdisplay,
    setinputdisplay,
    namecolor,
    setnamecolor,
    pointer,
    setpointer,
    professioncolor,
    setprofessioncolor,
    descriptioncolor,
    setdescriptioncolor,
    cvcolor,
    setcvcolor
    }}>
      {props.children}
    </Data.Provider>
  )
}
export { Data }
