import React, { useContext } from 'react'
import "./Main.css"
import {Data} from "../../context/Userdata"
export default function Main() {
    const b = useContext(Data)
    const mainfunc=(e,i)=>{
        e.stopPropagation();
        b.setinputdisplay(true);
        b.setpointer(i);
        b.setlinkdisplay(false)
    }
  return (
    <div className="main" style={{backgroundColor:b.maincolor}} onClick={(e)=>{mainfunc(e,0)}}>
        <div className="content">
            <input maxLength={25} type="text" className='name' style={{color:b.namecolor,backgroundColor:b.maincolor}} onClick={(e)=>{mainfunc(e,1)}} placeholder='Name Here'/>

            <input type="text" className='profession' style={{color:b.professioncolor,backgroundColor:b.maincolor}} onClick={(e)=>{mainfunc(e,2)}} placeholder='Profession'/>

            <textarea maxLength="300" cols="60" rows="6" className='description' style={{color:b.descriptioncolor,backgroundColor:b.maincolor}} onClick={(e)=>{mainfunc(e,3)}} placeholder='Lorem ipsum, dolor sit amet consectetur adipisicing elit. Magni omnis doloremque corporis necessitatibus id, magnam temporibus voluptatem blanditiis. Nobis debitis possimus, quaerat sunt nemo accusamus exercitationem quis. Fuga, ullam? Aperiam?'/>
            
            <button className="cv" style={{backgroundColor:b.cvcolor}} onClick={(e)=>{mainfunc(e,4)}}>
                Download Cv
            </button>
        </div>
        <div className="mainimage">
            <div className="imgbackground" style={{backgroundColor:b.mainimagecolor}} onClick={(e)=>{mainfunc(e,5)}}>
                <img src="https://res.cloudinary.com/dtws0vjlb/image/upload/v1689062624/avtars/cd4ihz1spsjg2jpwejqi.png" alt="none" />
            </div>
        </div>
    </div>
  )
}
