import React, { useState } from 'react'
import "./Register.css"
export default function Register() {
    const [userid,setuserid] = useState("")
    const [password,setpassword] = useState("")
    const create = (e) =>{
        e.preventDefault()
        // console.log("hi")

    }
    return (
        <div id='register'>
            <div>
                <h2>Create A New Account</h2>
                <form onSubmit={e=>{create(e)}}>
                    <label htmlFor="userid">Enter Your Mobile Number</label>
                    <input className='userid' type="text" placeholder='Type Number' value={userid} onChange={e=>{setuserid(e.target.value)}}/>
                    <label htmlFor="password">Create New Password</label>
                    <input className='password' type="text" placeholder='Type Password' value={password} onChange={e=>{setpassword(e.target.value)}}/>
                    <button disabled={(userid.length!==10 && password.length>8)?"":"true"} type="submit">Create</button>
                    <div>
                        <p>Already Have A Account !!</p>
                        <a href="/">Login</a>
                    </div>
                </form>
            </div>
        </div>
    )
}
