import React, { useContext } from 'react'
import {Data} from "../../context/Userdata"
import "./Takeinput.css"
export default function Takeinput() {
    const b = useContext(Data)
  return (
    <div className='input' style={b.inputdisplay===false?{top:"-50px"}:{top:"10px"}}>
        <input type="color" class="form-control form-control-color" id="exampleColorInput" value={b.maincolor} title="Choose your color" onChange={(e)=>{b.setmaincolor(e.target.value)}}></input>  
    </div>
  )
}
