import React, { createContext, useState} from 'react';
const Data = createContext()

export default function Userdata(props) {
    const [maincolor,setmaincolor] = useState("cyan")
    const [inputdisplay,setinputdisplay] = useState(false)
    const [namecolor,setnamecolor] = useState("black")
    const [professioncolor,setprofessioncolor] = useState("black")
    const [pointer,setpointer] = useState(1)
  return (
    <Data.Provider value={{maincolor,
    setmaincolor,
    inputdisplay,
    setinputdisplay,
    namecolor,
    setnamecolor,
    pointer,
    setpointer,
    professioncolor,
    setprofessioncolor
    }}>
      {props.children}
    </Data.Provider>
  )
}
export { Data }
