import React, { useContext, useState } from 'react'
import "./Create.css"
import { Data } from '../../context/Userdata'
import Errormessage from '../errormessage/Errormessage'

export default function Create() {
    const [loading, setloading] = useState(false)
    const b = useContext(Data)
    const func = async() => {
        let id = localStorage.getItem("userid")
        setloading(true)
        let data = {
            "maincolor": b.maincolor,
            "username": b.username,
            "namecolor": b.namecolor,
            "profession": b.profession,
            "professioncolor": b.professioncolor,
            "description": b.description,
            "descriptioncolor": b.descriptioncolor,
            "cvlink": b.cvlink,
            "cvcolor": b.cvcolor,
            "mainimage": b.mainimage,
            "mainimagecolor": b.mainimagecolor,
            "contactbackgroundcolor": b.contactbackgroundcolor,
            "contactcolor": b.contactcolor,
            "location": b.location,
            "email": b.email,
            "github": b.githublink,
            "telegram": b.telegramlink,
            "insta": b.insta,
            "leetcode": b.leetlink,
            "yt": b.ytlink,
            "ldin": b.ldinlink
        }
        data = JSON.stringify(data)
        let response = await fetch(`http://localhost:5000/update/${id}`,
        {
            method:"GET",
            headers:{"data":data}
        })
        response = await response.json()
        if(response.response==="successfull"){
            navigator.clipboard.writeText(`http://localhost:3000/user/${localStorage.getItem("userid")}`);
            <Errormessage message={"Link Coppied To Clipboard"}/>
        }
    }
    return (
        <button id='create' onClick={func}>create</button>
    )
}
