import React, { useState } from 'react'
import "./Register.css"
import Errormessage from '../errormessage/Errormessage'
export default function Register() {
    const [userid, setuserid] = useState("")
    const [password, setpassword] = useState("")
    const [useralreadyexists, setuseralradyexists] = useState(false)
    const create = (e) => {
        e.preventDefault()

        const func = async () => {
            let response = await fetch("http://localhost:5000/register/", {
                method: "GET",
                headers: {
                    'Accept': 'application/json',
                    'userid': userid,
                    'password': password
                }
            })
            response = await response.json()
            console.log(response)

            if (response.response === "user created successfully") {
                localStorage.setItem("userid", userid)
                localStorage.setItem("password", password)
                setuseralradyexists(false)
            }
            else {
                setuseralradyexists(true)
            }
        }
        func()

    }
    return (
        <div id='register'>
        {useralreadyexists?<Errormessage message={"User Alredy Exists"} func={setuseralradyexists}/>:""}
            <div>
                <h2>Create A New Account</h2>
                <form onSubmit={e => { create(e) }}>
                    <label htmlFor="userid">Enter Your Mobile Number</label>
                    <input className='userid' type="number" placeholder='Type 10 Digit Number' value={userid} onChange={e => { setuserid(e.target.value) }} />
                    <label htmlFor="password">Create New Password</label>
                    <input className='password' type="text" placeholder='Type Password min(8)' value={password} onChange={e => { setpassword(e.target.value) }} />
                    <button disabled={(userid.length !== 10 || password.length < 8) ? 1 : 0} type="submit">Create</button>
                    <div>
                        <p>Already Have A Account !!</p>
                        <a href="/">Login</a>
                    </div>
                </form>
            </div>
        </div>
    )
}
